import { RoundedBox, Scroll, ScrollControls } from "@react-three/drei";
import OurComponent from "./components/OurComponent";

function App() {

  return (
    <>
      <color attach="background" args={["#333333"]} />
      <ScrollControls pages={6} damping={0.1}>
        {/* Canvas contents in here will *not* scroll, but receive useScroll! */}
        <Scroll>
          {/* Canvas contents in here will scroll along */}
         <RoundedBox args={[1, 1, 1]} radius={.05} smoothness={4} >
          <meshPhongMaterial wireframe color="hotpink" />

         </RoundedBox>
        </Scroll>
        <Scroll html>
          {/* DOM contents in here will scroll along */}
          <h1>html in here (optional)</h1>
          <h1 style={{ position:"absolute", top: '100vh' }}>second page</h1>
          <h1 style={{ top: '200vh', position: 'absolute'}}>third page</h1>
        </Scroll>
      </ScrollControls>
      </>
  );
}

export default App;
